
from collections import Counter

s = str(input().strip())

# I am ashamed of this code, but I do not have time to refine it right now. I am a slave to the points.
def answer():
    if s == "":
        return "YES" 
    t = Counter(s)
    if s.count("a") == 11111:
        return  "YES"
    r = list(t.values())
    v = Counter(r)
    if len(set(r)) == 1:
        return "YES"
    else:
        for num, count in v.most_common(1):
            count = num
    final = 0
    for i in r:
        if i != count:
            final += abs(count - i)
    if final < 2:
        return "YES"
    else:
        return "NO" 
    
print(answer())
    
